import React from "react";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

// Sample Card Component for Dashboard Widgets
const DashboardCard = ({ title, value, icon, change, isIncrease }) => (
  <div className="bg-white p-6 rounded-lg shadow-md flex flex-col items-center justify-between w-40 h-40 m-2">
    <div className="flex items-center space-x-2">
      <span className={`text-2xl ${isIncrease ? 'text-green-500' : 'text-red-500'}`}>{icon}</span>
      <span className="text-gray-700 font-semibold text-lg">{title}</span>
    </div>
    <span className="text-2xl font-bold text-gray-900">{value}</span>
    <span className={`text-sm ${isIncrease ? 'text-green-500' : 'text-red-500'}`}>
      {change} Since last month
    </span>
  </div>
);

// Sample Chart Placeholder (replace with a real chart library, e.g., Chart.js or Recharts)
const SalesChart = () => (
  <div className="bg-white p-6 rounded-lg shadow-md w-full h-64">
    <p className="font-semibold text-gray-700">Sales</p>
    {/* Replace this div with an actual chart component */}
    <div className="h-full flex items-center justify-center text-gray-400">
      Sales Chart Placeholder
    </div>
  </div>
);

const TrafficSource = () => (
  <div className="bg-white p-6 rounded-lg shadow-md w-80 h-64">
    <p className="font-semibold text-gray-700">Traffic Source</p>
    {/* Replace this div with an actual chart component */}
    <div className="h-full flex items-center justify-center text-gray-400">
      Traffic Source Chart Placeholder
    </div>
  </div>
);

const DashboardLayout = ({ children, toggleDarkMode, darkMode, toggleSidebar }) => {
  const role = localStorage.getItem("role");
  const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

  return (
    <div className="flex bg-gray-100 min-h-screen">
      <SidebarComponent />
      <div className="flex-1">
        <Headers
          toggleDarkMode={toggleDarkMode}
          darkMode={darkMode}
          toggleSidebar={toggleSidebar}
        />
        
        <main className="p-4 space-y-4">
          {/* Dashboard Top Cards */}
          <div className="flex flex-wrap space-x-2">
            <DashboardCard title="Budget" value="$24k" icon="💰" change="12%" isIncrease={true} />
            <DashboardCard title="Total Customers" value="1.6k" icon="👥" change="16%" isIncrease={false} />
            <DashboardCard title="Task Progress" value="75.5%" icon="📈" change="5%" isIncrease={true} />
            <DashboardCard title="Total Profit" value="$15k" icon="💵" change="10%" isIncrease={true} />
          </div>

          {/* Sales Chart and Traffic Source */}
          <div className="flex flex-wrap space-x-4">
            <SalesChart />
            <TrafficSource />
          </div>

          {/* Other Children Components */}
          <div className="bg-white p-4 rounded-lg shadow-md mt-4">
            {children}
          </div>
        </main>
      </div>
    </div>
  );
};

export default DashboardLayout;
