// App.tsx

import React, { useState } from 'react';



const Logintry = () => {
  const [isActive, setIsActive] = useState(false);

  const toggleClass = () => {
    setIsActive(!isActive);
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-gray-300 to-indigo-100">
      <div className={`relative overflow-hidden w-[768px] max-w-full min-h-[480px] bg-white rounded-[30px] shadow-xl transition-all duration-600 ease-in-out ${isActive ? 'active' : ''}`}>
        {/* Sign Up Form */}
        <div className={`absolute top-0 h-full flex flex-col justify-center items-center w-1/2 transition-all duration-600 ease-in-out ${isActive ? 'translate-x-full opacity-100 z-5' : 'opacity-0 z-1'}`}>
          <form className="flex flex-col items-center px-10">
            <h1 className="text-2xl font-semibold">Create Account</h1>
          
            <span className="text-sm mb-4">or use your email for registration</span>
            <input type="text" placeholder="Name" className="bg-gray-200 p-2 rounded w-full mb-2" />
            <input type="email" placeholder="Email" className="bg-gray-200 p-2 rounded w-full mb-2" />
            <input type="password" placeholder="Password" className="bg-gray-200 p-2 rounded w-full mb-2" />
            <button className="bg-indigo-700 text-white py-2 px-10 rounded mt-4 uppercase font-semibold">Sign Up</button>
          </form>
        </div>

        {/* Sign In Form */}
        <div className={`absolute top-0 h-full flex flex-col justify-center items-center w-1/2 transition-all duration-600 ease-in-out ${isActive ? '-translate-x-full' : 'z-2'}`}>
          <form className="flex flex-col items-center px-10">
            <h1 className="text-2xl font-semibold">Sign In</h1>
           
            <span className="text-sm mb-4">or use your email for login</span>
            <input type="email" placeholder="Email" className="bg-gray-200 p-2 rounded w-full mb-2" />
            <input type="password" placeholder="Password" className="bg-gray-200 p-2 rounded w-full mb-2" />
           
            <button className="bg-indigo-700 text-white py-2 px-10 rounded mt-4 uppercase font-semibold">Sign In</button>
          </form>
        </div>

        {/* Toggle Container */}
        <div className={`absolute top-0 left-1/2 h-full w-1/2 overflow-hidden transition-all duration-600 ease-in-out rounded-[150px_0_0_100px] z-50 ${isActive ? '-translate-x-full rounded-[0_150px_100px_0]' : ''}`}>
          <div className="relative w-[200%] h-full bg-gradient-to-r from-indigo-500 to-indigo-700 text-white transition-all duration-600 ease-in-out">
            <div className={`absolute top-0 left-0 w-1/2 h-full flex flex-col justify-center items-center transition-all duration-600 ease-in-out ${isActive ? 'translate-x-0' : '-translate-x-full'}`}>
              <h1 className="text-3xl font-semibold">Welcome Back!</h1>
              <p className="text-sm my-4">Enter your personal details to use all of the site's features</p>
              <button onClick={toggleClass} className="bg-transparent border border-white text-white py-2 px-10 rounded uppercase font-semibold">Sign In</button>
            </div>
            <div className={`absolute top-0 right-0 w-1/2 h-full flex flex-col justify-center items-center transition-all duration-600 ease-in-out ${isActive ? 'translate-x-full' : 'translate-x-0'}`}>
              <h1 className="text-3xl font-semibold">Hello, Friend!</h1>
              <p className="text-sm my-4">Register with your personal details to use all of the site's features</p>
              <button onClick={toggleClass} className="bg-transparent border border-white text-white py-2 px-10 rounded uppercase font-semibold">Sign Up</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Logintry;
