import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Logo2 from "../../../assets/Logo2.png";
import { motion } from 'framer-motion';
import axios from 'axios';
import jwtDecode from 'jwt-decode';  // Perhatikan ini, import jwtDecode seharusnya tanpa destructuring

const Login = ({ closeModal }) => {  // Tambahkan closeModal sebagai prop
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    
    try {
      const response = await axios.post('http://localhost:5000/login', {
        email,
        password
      });
  
      const { token, role } = response.data;
      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
  
      const decodedToken = jwtDecode(token);

      if (decodedToken.role === 'admin') {
        navigate('/DashboardAdmin');
      } else if (decodedToken.role === 'staff') {
        navigate('/dashboard');
      }

      closeModal(); // Tutup modal setelah login berhasil

    } catch (error) {
      if (error.response && error.response.status === 400) {
        setError('Password salah!');
      } else if (error.response && error.response.status === 404) {
        setError('User tidak ditemukan!');
      } else {
        setError('Terjadi kesalahan pada server.');
      }
    }
  };

  return (
    <div className="flex flex-col">
      <img src={Logo2} alt="Logo" className="h-20 w-20 md:h-28 md:w-32 mx-auto" />
      <h2 className="text-2xl md:text-3xl font-bold mb-4 md:mb-6 mx-auto">
        {isLogin ? 'Login' : 'Reset Password'}
      </h2>

      <form className="space-y-4" onSubmit={handleLogin}>
        {isLogin ? (
          <>
            <input
              type="email"
              placeholder="Enter your Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            {error && <p className="text-red-500">{error}</p>}
            <button
              type="submit"
              className="w-full py-3 bg-purple-600 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white rounded-lg hover:bg-purple-700 transition duration-300"
            >
              Sign In
            </button>
          </>
        ) : (
          <>
            <input
              type="email"
              placeholder="Enter your Email to reset password"
              className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
            />
            <button
              type="submit"
              className="w-full py-3 bg-purple-600 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white rounded-lg hover:bg-purple-700 transition duration-300"
            >
              Reset Password
            </button>
          </>
        )}
      </form>
    </div>
  );
};

export default Login;
