import React, { useState, useEffect } from "react";
import axios from "axios";
import { FilePlus, Search, Trash2 } from "lucide-react"; // Import ikon dari Lucide
import CrudButton from "../../Button/CrudButton";

const OrderStock = () => {
  const [orders, setOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [newOrder, setNewOrder] = useState({
    noPembelian: "",
    date: "",
    kodebarang: "",
    barcode: "",
    namaBarang: "",
    hargaBeli: "",
    jumlahBeli: "",
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get("http://localhost:5000/orders");
      const sortedOrders = response.data.sort(
        (a, b) => new Date(b.date) - new Date(a.date)
      );
      setOrders(sortedOrders);
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  const handleAddOrder = async (e) => {
    e.preventDefault();
    try {
      const payload = {
        noPembelian: newOrder.noPembelian,
        date: newOrder.date,
        kodebarang: newOrder.kodebarang,
        barcode: newOrder.barcode,
        namaBarang: newOrder.namaBarang,
        hargaBeli: parseInt(newOrder.hargaBeli, 10),
        jumlahBeli: parseInt(newOrder.jumlahBeli, 10),
      };

      if (isEditing) {
        await axios.patch(`http://localhost:5000/orders/${editId}`, payload);
        setOrders(
          orders.map((order) =>
            order.id === editId ? { ...newOrder, id: editId } : order
          )
        );
        setIsEditing(false);
      } else {
        const response = await axios.post("http://localhost:5000/orders", payload);
        setOrders([...orders, { ...payload, id: response.data.id }]);
      }

      setNewOrder({
        noPembelian: "",
        date: "",
        kodebarang: "",
        barcode: "",
        namaBarang: "",
        hargaBeli: "",
        jumlahBeli: "",
      });
    } catch (error) {
      console.error("Error adding/updating order:", error);
    }
  };

  const handleEditOrder = (id) => {
    const orderToEdit = orders.find((order) => order.id === id);
    setNewOrder(orderToEdit);
    setEditId(id);
    setIsEditing(true);
  };

  const handleDeleteOrder = async (id) => {
    if (window.confirm("Apakah Anda yakin ingin menghapus data ini?")) {
      try {
        await axios.delete(`http://localhost:5000/orders/${id}`);
        setOrders(orders.filter((order) => order.id !== id));
      } catch (error) {
        console.error("Error deleting order:", error);
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrder({ ...newOrder, [name]: value });
  };

  const filteredOrders = orders.filter((order) => {
    return (
      order.kodebarang.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.namaBarang.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  return (
    <div className="container mx-auto p-6 mt-20">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold text-gray-800">Order Stok</h1>
        <div className="relative w-full max-w-xs">
          <input
            type="text"
            className="w-full pl-10 pr-4 py-2 rounded-lg border border-gray-300 focus:ring-2 focus:ring-blue-500 focus:outline-none"
            placeholder="Cari Kode Barang / Nama Barang"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Search className="absolute top-3 left-3 text-gray-400" size={20} />
        </div>
      </div>

      {/* Form */}
      <form onSubmit={handleAddOrder} className="bg-white p-6 rounded-lg shadow mb-6">
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            name="noPembelian"
            placeholder="No Pembelian"
            value={newOrder.noPembelian}
            onChange={handleInputChange}
            required
            className="border p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
          <input
            type="date"
            name="date"
            value={newOrder.date}
            onChange={handleInputChange}
            required
            className="border p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
          <input
            type="text"
            name="kodebarang"
            placeholder="Kode Barang"
            value={newOrder.kodebarang}
            onChange={handleInputChange}
            required
            className="border p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
          <input
            type="text"
            name="barcode"
            placeholder="Barcode"
            value={newOrder.barcode}
            onChange={handleInputChange}
            required
            className="border p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
          <input
            type="text"
            name="namaBarang"
            placeholder="Nama Barang"
            value={newOrder.namaBarang}
            onChange={handleInputChange}
            required
            className="border p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
          <input
            type="number"
            name="hargaBeli"
            placeholder="Harga Beli"
            value={newOrder.hargaBeli}
            onChange={handleInputChange}
            required
            className="border p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
          <input
            type="number"
            name="jumlahBeli"
            placeholder="Jumlah Beli"
            value={newOrder.jumlahBeli}
            onChange={handleInputChange}
            required
            className="border p-3 rounded-lg focus:ring-2 focus:ring-blue-500 focus:outline-none"
          />
        </div>
        <button
          type="submit"
          className={`mt-4 px-4 py-2 rounded-lg text-white font-medium ${
            isEditing ? "bg-yellow-500 hover:bg-yellow-600" : "bg-blue-500 hover:bg-blue-600"
          }`}
        >
          {isEditing ? "Update Order" : "Tambah Order"}
        </button>
      </form>

      {/* Tabel */}
      <div className="bg-white rounded-lg shadow overflow-x-auto">
        <table className="min-w-full border-collapse">
          <thead>
            <tr className="bg-gray-100 text-gray-800">
              {[
                "No Pembelian",
                "Tanggal",
                "Kode Barang",
                "Barcode",
                "Nama Barang",
                "Harga Beli",
                "Jumlah Beli",
                "Actions",
              ].map((header) => (
                <th key={header} className="px-4 py-2 text-left font-medium">
                  {header}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {filteredOrders.map((order) => (
              <tr key={order.id} className="hover:bg-gray-50">
                <td className="border px-4 py-2">{order.noPembelian}</td>
                <td className="border px-4 py-2">
                  {new Date(order.date).toLocaleDateString()}
                </td>
                <td className="border px-4 py-2">{order.kodebarang}</td>
                <td className="border px-4 py-2">{order.barcode}</td>
                <td className="border px-4 py-2">{order.namaBarang}</td>
                <td className="border px-4 py-2">{order.hargaBeli}</td>
                <td className="border px-4 py-2">{order.jumlahBeli}</td>
                <td className="border px-4 py-2 space-x-2">
                  <CrudButton
                    icon={FilePlus}
                    label="Edit"
                    onClick={() => handleEditOrder(order.id)}
                    buttonStyle="primary"
                  />
                  <CrudButton
                    icon={Trash2}
                    label="Delete"
                    onConfirm={() => handleDeleteOrder(order.id)}
                    confirmMessage="Apakah Anda yakin ingin menghapus data ini?"
                    buttonStyle="danger"
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default OrderStock;
