import React, { useState } from "react";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin";
import Sidebars from "../Sidebar/staff/Sidebars";
import Headers from "../Sidebar/Headers";

const DashboardLayout = ({ toggleDarkMode, darkMode }) => {
    const [isSidebarOpen, setIsSidebarOpen] = useState(false);
    const role = localStorage.getItem("role");
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

    // Fungsi untuk membuka/menutup sidebar
    const toggleSidebar = () => {
        setIsSidebarOpen((prev) => !prev);
    };

    return (
        <div className="flex min-h-screen">
            {/* Sidebar: Kontrol berdasarkan isSidebarOpen */}
            <div
                className={clsx(
                    "fixed z-40 inset-y-0 left-0 transform bg-white dark:bg-gray-800 shadow-lg",
                    "transition-transform duration-300 ease-in-out",
                    isSidebarOpen ? "translate-x-0" : "-translate-x-full", // Atur posisi sidebar
                    "lg:translate-x-0" // Selalu tampil di layar besar (LG)
                )}
            >
                <SidebarComponent
                    isSidebarOpen={isSidebarOpen}
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                />
            </div>

            {/* Main Content */}
            <div className="flex-1 flex flex-col">
                {/* Header */}
                <Headers
                    toggleDarkMode={toggleDarkMode}
                    darkMode={darkMode}
                    toggleSidebar={toggleSidebar} // Oper fungsi ke Header
                />
                <main className="flex-1 p-4 bg-gray-100">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
