import React, { useState, useEffect } from 'react';
import axios from 'axios';

const OrderStock = () => {
  const [stocks, setStocks] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [newStock, setNewStock] = useState({
    id: '',
    noPembelian: '',
    kodeBarang: '',
    barcode: '',
    namaBarang: '',
    hargaBeli: '',
    image: null,
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchStocks();
  }, []);

  const fetchStocks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/orders');
      setStocks(response.data);
    } catch (error) {
      console.error('Error fetching stocks:', error);
    }
  };

  const handleAddStock = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        await axios.patch(`http://localhost:5000/orders/${editId}`, newStock);
        setStocks(stocks.map(stock => stock.id === editId ? { ...newStock, id: editId } : stock));
        setIsEditing(false);
      } else {
        const response = await axios.post('http://localhost:5000/orders', newStock);
        setStocks([...stocks, { ...newStock, id: response.data.id }]);
      }
      setNewStock({
        id: '',
        noPembelian: '',
        kodeBarang: '',
        barcode: '',
        namaBarang: '',
        hargaBeli: '',
        image: null,
      });
    } catch (error) {
      console.error('Error adding/updating stock:', error);
    }
  };

  const handleEditStock = (id) => {
    const stockToEdit = stocks.find(stock => stock.id === id);
    setNewStock(stockToEdit);
    setEditId(id);
    setIsEditing(true);
  };

  const handleDeleteStock = async (id) => {
    if (window.confirm('Apakah Anda yakin ingin menghapus data ini?')) {
      try {
        await axios.delete(`http://localhost:5000/orders/${id}`);
        setStocks(stocks.filter(stock => stock.id !== id));
      } catch (error) {
        console.error('Error deleting stock:', error);
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStock({ ...newStock, [name]: value });
  };

  const handleImageUpload = (e) => {
    setNewStock({ ...newStock, image: URL.createObjectURL(e.target.files[0]) });
  };

  const filteredStocks = stocks.filter((stock) => {
    return (
      stock.kodeBarang.toLowerCase().includes(searchTerm.toLowerCase()) ||
      stock.namaBarang.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  return (
    <div className="container mx-auto p-5 mt-20">
      <h1 className="text-3xl font-bold mb-4">Order Stok</h1>

      <input
        type="text"
        className="border p-2 mb-4 w-full"
        placeholder="Cari berdasarkan Kode Barang atau Nama Barang"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <form onSubmit={handleAddStock} className="mb-5">
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            name="noPembelian"
            placeholder="No Pembelian"
            value={newStock.noPembelian}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="kodeBarang"
            placeholder="Kode Barang"
            value={newStock.kodeBarang}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="barcode"
            placeholder="Barcode"
            value={newStock.barcode}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="namaBarang"
            placeholder="Nama Barang"
            value={newStock.namaBarang}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="number"
            name="hargaBeli"
            placeholder="Harga Beli"
            value={newStock.hargaBeli}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="file"
            name="image"
            onChange={handleImageUpload}
            className="border p-2"
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white p-2 mt-4">
          {isEditing ? 'Update Stock' : 'Tambah Stok'}
        </button>
      </form>

      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2">No Pembelian</th>
            <th className="py-2">Kode Barang</th>
            <th className="py-2">Barcode</th>
            <th className="py-2">Nama Barang</th>
            <th className="py-2">Harga Beli</th>
            <th className="py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredStocks.map((stock) => (
            <tr key={stock.id}>
              <td className="border px-4 py-2">{stock.noPembelian}</td>
              <td className="border px-4 py-2">{stock.kodeBarang}</td>
              <td className="border px-4 py-2">{stock.barcode}</td>
              <td className="border px-4 py-2">{stock.namaBarang}</td>
              <td className="border px-4 py-2">{stock.hargaBeli}</td>
              <td className="border px-4 py-2">
                <button onClick={() => handleEditStock(stock.id)} className="bg-yellow-500 text-white p-2 mr-2">Edit</button>
                <button onClick={() => handleDeleteStock(stock.id)} className="bg-red-500 text-white p-2">Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrderStock;
