import React, { useState, useEffect } from "react";
import axios from "axios";
import { Search, Edit, Trash2 } from "lucide-react"; // Import ikon dari Lucide React

const OrderStock = () => {
  const [orders, setOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get("http://localhost:5000/orders");
      setOrders(response.data);
    } catch (error) {
      console.error("Error fetching orders:", error);
    }
  };

  const handleDeleteOrder = async (id) => {
    if (window.confirm("Apakah Anda yakin ingin menghapus data ini?")) {
      try {
        await axios.delete(`http://localhost:5000/orders/${id}`);
        setOrders(orders.filter((order) => order.id !== id));
      } catch (error) {
        console.error("Error deleting order:", error);
      }
    }
  };

  const filteredOrders = orders.filter((order) => {
    return (
      order.customer?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.orderNumber?.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  return (
    <div className="bg-gray-50 min-h-screen p-6">
      {/* Header */}
      <div className="bg-white p-6 rounded-lg shadow-sm">
        <div className="flex justify-between items-center mb-4">
          <h1 className="text-2xl font-bold">Orders</h1>
          <div className="relative">
            <input
              type="text"
              className="border border-gray-300 rounded-lg pl-10 pr-4 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Search orders..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <Search className="absolute top-2.5 left-3 text-gray-400" size={20} />
          </div>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-4 gap-4 mb-6">
          <div className="bg-white p-4 rounded-lg shadow-md text-center">
            <h2 className="text-sm text-gray-500">Total Orders</h2>
            <p className="text-lg font-bold">{orders.length}</p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow-md text-center">
            <h2 className="text-sm text-gray-500">Order Items</h2>
            <p className="text-lg font-bold">{orders.reduce((sum, order) => sum + order.items, 0)} items</p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow-md text-center">
            <h2 className="text-sm text-gray-500">Fulfilled</h2>
            <p className="text-lg font-bold">
              {
                orders.filter((order) => order.fulfillment === "Fulfilled")
                  .length
              }
            </p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow-md text-center">
            <h2 className="text-sm text-gray-500">Pending</h2>
            <p className="text-lg font-bold">
              {
                orders.filter((order) => order.fulfillment === "Unfulfilled")
                  .length
              }
            </p>
          </div>
        </div>

        {/* Orders Table */}
        <div className="overflow-x-auto bg-white rounded-lg shadow-md">
          <table className="min-w-full table-auto">
            <thead className="bg-gray-100 text-gray-600 text-sm uppercase font-medium">
              <tr>
                <th className="py-3 px-6 text-left">Order</th>
                <th className="py-3 px-6 text-left">Date</th>
                <th className="py-3 px-6 text-left">Customer</th>
                <th className="py-3 px-6 text-left">Payment</th>
                <th className="py-3 px-6 text-left">Total</th>
                <th className="py-3 px-6 text-left">Items</th>
                <th className="py-3 px-6 text-left">Fulfillment</th>
                <th className="py-3 px-6 text-center">Actions</th>
              </tr>
            </thead>
            <tbody className="text-gray-600 text-sm">
              {filteredOrders.map((order) => (
                <tr
                  key={order.id}
                  className="border-b hover:bg-gray-50 transition"
                >
                  <td className="py-3 px-6">#{order.orderNumber}</td>
                  <td className="py-3 px-6">{order.date}</td>
                  <td className="py-3 px-6">{order.customer}</td>
                  <td className="py-3 px-6">
                    <span
                      className={`px-2 py-1 rounded text-xs ${
                        order.payment === "Pending"
                          ? "bg-yellow-100 text-yellow-600"
                          : "bg-green-100 text-green-600"
                      }`}
                    >
                      {order.payment}
                    </span>
                  </td>
                  <td className="py-3 px-6">${order.total}</td>
                  <td className="py-3 px-6">{order.items} items</td>
                  <td className="py-3 px-6">
                    <span
                      className={`px-2 py-1 rounded text-xs ${
                        order.fulfillment === "Unfulfilled"
                          ? "bg-red-100 text-red-600"
                          : "bg-green-100 text-green-600"
                      }`}
                    >
                      {order.fulfillment}
                    </span>
                  </td>
                  <td className="py-3 px-6 text-center">
                    <button className="text-blue-500 hover:underline mr-2">
                      <Edit size={16} />
                    </button>
                    <button
                      onClick={() => handleDeleteOrder(order.id)}
                      className="text-red-500 hover:underline"
                    >
                      <Trash2 size={16} />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default OrderStock;
