import { Routes, Route } from "react-router-dom";
import { useState, useEffect } from "react";
import UserList from "../components/Crud User/UserList";
import AddUser from "../components/Crud User/AddUser";
import EditUser from "../components/Crud User/EditUser";
import Login from "../components/Page/Home/Login";
import Register from "../components/Page/Home/Register";
import Home from '../components/Page/Home/Home';
import Navbar from "../components/Navbar/Navbar";
import Headers from "../components/Page/Dashboard/Headers";
import Layout from "../components/Layout/AppLayout";
import DashboardLayout from "../components/Layout/DashboardLayout";

function AppRoutes() {
  const [darkMode, setDarkMode] = useState(false);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
    }
  }, [darkMode]);

  return (
    <div className={`font-quickSand`}>
      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/Login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* Routes that use the regular Navbar */}
        <Route element={<Layout />}>
          <Route path="/users" element={<UserList />} />
          <Route path="add" element={<AddUser />} />
          <Route path="edit/:id" element={<EditUser />} />
        </Route>

        {/* Dashboard Routes - Using Headers */}
        <Route element={<DashboardLayout />}>
          <Route
            path="/dashboard"
            element={
              <>
                <Headers darkMode={darkMode} toggleDarkMode={toggleDarkMode} />
                {/* ...Other dashboard content... */}
              </>
            }
          />
        </Route>
      </Routes>
    </div>
  );
}

export default AppRoutes;
