import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Search } from 'lucide-react'; // Import ikon Search dari Lucide

const OrderStock = () => {
  const [orders, setOrders] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [newOrder, setNewOrder] = useState({
    noPembelian: '',
    date: '',
    kodebarang: '',
    barcode: '',
    namaBarang: '',
    hargaBeli: '',
    jumlahBeli: '',
    image: null // Tambahkan state untuk menyimpan file gambar
  });
  const [isEditing, setIsEditing] = useState(false);
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/orders');
      setOrders(response.data);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handleFileChange = (e) => {
    setNewOrder({ ...newOrder, image: e.target.files[0] }); // Mengubah file gambar
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrder({ ...newOrder, [name]: value });
  };

  const handleAddOrder = async (e) => {
    e.preventDefault();
    
    const formData = new FormData(); // Gunakan FormData untuk mengirim data termasuk file gambar
    formData.append('noPembelian', newOrder.noPembelian);
    formData.append('date', newOrder.date);
    formData.append('kodebarang', newOrder.kodebarang);
    formData.append('barcode', newOrder.barcode);
    formData.append('namaBarang', newOrder.namaBarang);
    formData.append('hargaBeli', newOrder.hargaBeli);
    formData.append('jumlahBeli', newOrder.jumlahBeli);
    if (newOrder.image) {
      formData.append('image', newOrder.image); // Tambahkan file gambar ke form data
    }

    try {
      if (isEditing) {
        await axios.patch(`http://localhost:5000/orders/${editId}`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        setIsEditing(false);
      } else {
        await axios.post('http://localhost:5000/orders', formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
      }

      // Reset form setelah submit
      setNewOrder({
        noPembelian: '',
        date: '',
        kodebarang: '',
        barcode: '',
        namaBarang: '',
        hargaBeli: '',
        jumlahBeli: '',
        image: null
      });
      fetchOrders();
    } catch (error) {
      console.error('Error adding/updating order:', error);
    }
  };

  const handleEditOrder = (id) => {
    const orderToEdit = orders.find(order => order.id === id);
    setNewOrder(orderToEdit);
    setEditId(id);
    setIsEditing(true);
  };

  return (
    <div className="container mx-auto p-5 mt-20">
      <form onSubmit={handleAddOrder} className="mb-5" encType="multipart/form-data">
        <div className="grid grid-cols-2 gap-4">
          <input
            type="text"
            name="noPembelian"
            placeholder="No Pembelian"
            value={newOrder.noPembelian}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="date"
            name="date"
            placeholder="Date"
            value={newOrder.date}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="kodebarang"
            placeholder="Kode Barang"
            value={newOrder.kodebarang}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="barcode"
            placeholder="Barcode"
            value={newOrder.barcode}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="text"
            name="namaBarang"
            placeholder="Nama Barang"
            value={newOrder.namaBarang}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="number"
            name="hargaBeli"
            placeholder="Harga Beli"
            value={newOrder.hargaBeli}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="number"
            name="jumlahBeli"
            placeholder="Jumlah Beli"
            value={newOrder.jumlahBeli}
            onChange={handleInputChange}
            required
            className="border p-2"
          />
          <input
            type="file"
            name="image"
            onChange={handleFileChange} // Menghandle upload gambar
            className="border p-2"
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white p-2 mt-4">
          {isEditing ? 'Update Order' : 'Tambah Order'}
        </button>
      </form>

      {/* Daftar Order */}
      <table className="table-auto w-full">
        <thead>
          <tr>
            <th>No</th>
            <th>No Pembelian</th>
            <th>Nama Barang</th>
            <th>Jumlah Beli</th>
            <th>Gambar</th>
            <th>Aksi</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((order, index) => (
            <tr key={order.id}>
              <td>{index + 1}</td>
              <td>{order.noPembelian}</td>
              <td>{order.namaBarang}</td>
              <td>{order.jumlahBeli}</td>
              <td>
                {order.imageUrl ? (
                  <img src={`http://localhost:5000${order.imageUrl}`} alt={order.namaBarang} className="w-16 h-16" />
                ) : (
                  'No Image'
                )}
              </td>
              <td>
                <button onClick={() => handleEditOrder(order.id)} className="bg-yellow-500 text-white p-1">
                  Edit
                </button>
                {/* Tambahkan tombol hapus atau aksi lainnya di sini */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OrderStock;
