import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin"; // Admin Sidebar
import Sidebars from "../Sidebar/staff/Sidebars"; // Staff Sidebar
import Headers from "../Sidebar/Headers"; // Header Component

const DashboardLayout = () => {
    const [darkMode, setDarkMode] = useState(false);
    const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Default sidebar tertutup di mobile
    const role = localStorage.getItem("role"); // Ambil role dari localStorage
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;

    const toggleSidebar = () => {
        setIsSidebarOpen((prev) => !prev);
    };

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    return (
        <div className="flex min-h-screen bg-gray-100 overflow-x-hidden">
            {/* Overlay for Sidebar in Mobile */}
            {isSidebarOpen && (
                <div
                    className="fixed inset-0 bg-black bg-opacity-50 z-30 lg:hidden"
                    onClick={toggleSidebar}
                ></div>
            )}

            {/* Sidebar */}
            <div
                className={clsx(
                    "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
                    isSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Mobile: Geser ke kiri jika tertutup
                    "lg:translate-x-0 lg:w-64", // Desktop: Sidebar selalu terlihat
                    "lg:relative lg:z-auto" // Desktop: Tidak overlay
                )}
            >
                <SidebarComponent
                    isSidebarOpen={isSidebarOpen}
                    darkMode={darkMode}
                    toggleDarkMode={toggleDarkMode}
                />
            </div>

            {/* Header */}
            <div
                className={clsx(
                    "fixed w-full z-50 bg-white dark:bg-gray-800 shadow-md transition-all duration-300 ease-in-out",
                    isSidebarOpen ? "lg:ml-64" : "lg:ml-0"
                )}
            >
                <Headers
                    toggleDarkMode={toggleDarkMode}
                    darkMode={darkMode}
                    toggleSidebar={toggleSidebar}
                />
            </div>

            {/* Main Content */}
            <div
                className={clsx(
                    "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
                    isSidebarOpen ? "lg:ml-64" : "lg:ml-0"
                )}
            >
                <main className="flex-1 p-4">
                    <Outlet />
                </main>
            </div>
        </div>
    );
};

export default DashboardLayout;
