import React, { useState } from "react";
import clsx from "clsx";
import { Outlet } from "react-router-dom";
import SidebarAdmin from "../Sidebar/admin/SidebarAdmin"; // Admin Sidebar
import Sidebars from "../Sidebar/staff/Sidebars"; // Staff Sidebar
import Headers from "../Sidebar/Headers"; // Header Component
const DashboardLayout = () => {
    const [darkMode, setDarkMode] = useState(false);
    const [isMobileSidebarOpen, setIsMobileSidebarOpen] = useState(true); // Mobile sidebar
    const [isDesktopSidebarOpen, setIsDesktopSidebarOpen] = useState(true); // Desktop sidebar
  
    const role = localStorage.getItem("role"); // Get role from localStorage
    const SidebarComponent = role === "admin" ? SidebarAdmin : Sidebars;
  
    // Mobile sidebar toggle
    const toggleMobileSidebar = () => {
      setIsMobileSidebarOpen((prev) => !prev);
    };
  
    // Desktop sidebar toggle
    const toggleDesktopSidebar = () => {
      setIsDesktopSidebarOpen((prev) => !prev);
    };
  
    return (
      <div className="flex min-h-screen bg-gray-100">
        {/* Sidebar */}
        <div
          className={clsx(
            "fixed z-40 inset-y-0 left-0 bg-white dark:bg-gray-800 shadow-lg transition-transform duration-300 ease-in-out",
            isMobileSidebarOpen ? "translate-x-0 w-64" : "-translate-x-full", // Mobile sidebar
            isDesktopSidebarOpen ? "lg:w-64" : "lg:w-0" // Desktop sidebar
          )}
        >
          <SidebarComponent
            isSidebarOpen={isMobileSidebarOpen || isDesktopSidebarOpen}
            darkMode={darkMode}
            toggleDarkMode={() => setDarkMode(!darkMode)}
          />
        </div>
  
        {/* Main Header */}
        <div className={`${darkMode && "dark"}`}>
          <Headers
            toggleDarkMode={() => setDarkMode(!darkMode)}
            darkMode={darkMode}
            toggleMobileSidebar={toggleMobileSidebar}
            toggleDesktopSidebar={toggleDesktopSidebar}
          />
        </div>
  
        {/* Main Content */}
        <div
          className={clsx(
            "flex-1 flex flex-col transition-all duration-300 ease-in-out overflow-x-hidden",
            isDesktopSidebarOpen ? "lg:ml-64" : "lg:ml-0" // Adjust main content for desktop sidebar
          )}
        >
          <main className="flex-1 p-4">
            <Outlet />
          </main>
        </div>
      </div>
    );
  };
  
  export default DashboardLayout;
