import React, { useState } from 'react';
import Logo2 from "../../../assets/Logo2.png";

const Login = () => {
  // Use state to toggle between login and reset password
  const [isLogin, setIsLogin] = useState(true);

  // Toggle between login and reset password
  const toggleForm = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100 dark:bg-gray-800">
      <div className="relative bg-white rounded-lg shadow-lg flex w-[900px] h-[500px] overflow-hidden transition-transform duration-500">
        
        {/* Sliding container */}
        <div className={`absolute w-full h-full flex transition-transform duration-500 ${isLogin ? 'translate-x-0' : '-translate-x-full'}`}>
          
          {/* Left Side - Sign In */}
          <div className="flex-1 bg-gray-100 dark:bg-gray-700 p-10 flex flex-col justify-center">
            <img src={Logo2} alt="Logo" className="h-28 w-32 mx-auto" />
            <h2 className="text-3xl font-bold mb-6 mx-auto">Login</h2>

            {/* Sign In Form */}
            <form className="space-y-4">
              <input
                type="email"
                placeholder="Enter your Email"
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
              <input
                type="password"
                placeholder="Password"
                className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
              <button
                type="submit"
                className="w-full py-3 bg-purple-600 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white rounded-lg hover:bg-purple-700 transition duration-300"
              >
                Sign In
              </button>
            </form>
          </div>

          {/* Right Side - Forget Password */}
          <div className="flex-1 bg-gradient-to-r from-purple-600 to-purple-400 dark:bg-gradient-to-br dark:from-purple-800 dark:to-blue-900 text-white p-10 flex flex-col justify-center items-center">
            <h2 className="text-3xl font-bold mb-4">Forget Password?</h2>
            <p className="mb-6 text-center">Register with your personal details to use all of the site's features</p>
            <button
              onClick={toggleForm}
              className="py-3 px-8 bg-white dark:bg-purple-600 text-purple-600 dark:text-white rounded-lg hover:bg-gray-100 transition duration-300"
            >
              Reset
            </button>
          </div>
        </div>

        {/* Sliding Button Panel */}
        <div className="absolute right-0 h-full w-1/2 flex flex-col justify-center items-center bg-purple-600 dark:bg-purple-700 text-white transition-transform duration-500">
          {isLogin ? (
            <>
              <h2 className="text-2xl font-bold mb-4">Welcome Back!</h2>
              <p className="mb-4 text-center">To keep connected with us please login with your personal info.</p>
              <button
                onClick={toggleForm}
                className="py-3 px-8 bg-white dark:bg-purple-600 text-purple-600 dark:text-white rounded-lg hover:bg-gray-100 transition duration-300"
              >
                Reset Password
              </button>
            </>
          ) : (
            <>
              <h2 className="text-2xl font-bold mb-4">Hello, Friend!</h2>
              <p className="mb-4 text-center">Enter your email to reset your password.</p>
              <button
                onClick={toggleForm}
                className="py-3 px-8 bg-white dark:bg-purple-600 text-purple-600 dark:text-white rounded-lg hover:bg-gray-100 transition duration-300"
              >
                Back to Login
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default Login;
